import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Nav, Table, Form, Button, Card, Row, Col, ListGroup, InputGroup } from 'react-bootstrap';
import { toast } from 'react-toastify';
import * as XLSX from 'xlsx';
import { Link } from 'react-router-dom';

const PlDashboard = () => {
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [classes, setClasses] = useState([]);
  const [filteredClasses, setFilteredClasses] = useState([]);
  const [reports, setReports] = useState([]);
  const [filteredReports, setFilteredReports] = useState([]);
  const [monitoring, setMonitoring] = useState({ avg_attendance: 0, report_count: 0 });
  const [ratings, setRatings] = useState([]);
  const [filteredRatings, setFilteredRatings] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [filteredLecturers, setFilteredLecturers] = useState([]);
  const [formData, setFormData] = useState({ name: '', code: '', stream: '', description: '' });
  const [assignData, setAssignData] = useState({ course_id: '', lecturer_id: '', venue: '', scheduled_time: '', total_students: '', stream: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('courses');

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        toast.error('No token found. Please log in again.');
        return;
      }
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const resCourses = await axios.get('http://localhost:5000/courses', config);
        setCourses(resCourses.data || []);
        setFilteredCourses(resCourses.data || []);
        const resClasses = await axios.get('http://localhost:5000/classes', config);
        setClasses(resClasses.data || []);
        setFilteredClasses(resClasses.data || []);
        const resReports = await axios.get('http://localhost:5000/reports', config);
        setReports(resReports.data || []);
        setFilteredReports(resReports.data || []);
        const resMonitoring = await axios.get('http://localhost:5000/monitoring', config);
        setMonitoring(resMonitoring.data || { avg_attendance: 0, report_count: 0 });
        const resRatings = await axios.get('http://localhost:5000/ratings', config);
        setRatings(resRatings.data || []);
        setFilteredRatings(resRatings.data || []);
        const resLecturers = await axios.get('http://localhost:5000/users/lecturers', config);
        setLecturers(resLecturers.data || []);
        setFilteredLecturers(resLecturers.data || []);
      } catch (err) {
        console.error('Fetch error in PlDashboard:', err.response?.data || err.message);
        toast.error(err.response?.data?.message || 'Error fetching data');
      }
    };
    fetchData();
  }, []);

  // Enhanced search functionality
  useEffect(() => {
    if (activeTab === 'courses') {
      const filtered = courses.filter(course =>
        course.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.code?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.stream?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.description?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredCourses(filtered);
    } else if (activeTab === 'reports') {
      const filtered = reports.filter(report =>
        report.lecturer_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.course_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.week?.toString().includes(searchTerm) ||
        report.prl_feedback?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.status?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredReports(filtered);
    } else if (activeTab === 'classes') {
      const filtered = classes.filter(classItem =>
        classItem.course_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.lecturer_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.venue?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.stream?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.scheduled_time?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredClasses(filtered);
    } else if (activeTab === 'lecturers') {
      const filtered = lecturers.filter(lecturer =>
        lecturer.username?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lecturer.stream?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        lecturer.email?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredLecturers(filtered);
    } else if (activeTab === 'rating') {
      const filtered = ratings.filter(rating =>
        rating.type?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        rating.comment?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        rating.rating?.toString().includes(searchTerm) ||
        rating.created_at?.includes(searchTerm)
      );
      setFilteredRatings(filtered);
    }
  }, [searchTerm, courses, reports, classes, lecturers, ratings, activeTab]);

  const handleCourseSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return toast.error('No token found');
    if (!formData.name || !formData.code || !formData.stream) return toast.error('Please fill all course fields');
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post('http://localhost:5000/courses', formData, config);
      toast.success('Course added');
      const resCourses = await axios.get('http://localhost:5000/courses', config);
      setCourses(resCourses.data || []);
      setFilteredCourses(resCourses.data || []);
      setFormData({ name: '', code: '', stream: '', description: '' });
    } catch (err) {
      console.error('Course submit error:', err.response?.data);
      toast.error(err.response?.data?.message || 'Error adding course');
    }
  };

  const handleAssignSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return toast.error('No token found');
    if (!assignData.course_id || !assignData.lecturer_id || !assignData.venue || !assignData.scheduled_time || !assignData.total_students || !assignData.stream) {
      return toast.error('Please fill all assignment fields');
    }
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post('http://localhost:5000/assign-lecturer', assignData, config);
      toast.success('Lecturer assigned');
      const resClasses = await axios.get('http://localhost:5000/classes', config);
      setClasses(resClasses.data || []);
      setFilteredClasses(resClasses.data || []);
      setAssignData({ course_id: '', lecturer_id: '', venue: '', scheduled_time: '', total_students: '', stream: '' });
    } catch (err) {
      console.error('Assign submit error:', err.response?.data);
      toast.error(err.response?.data?.message || 'Error assigning lecturer');
    }
  };

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filteredReports);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Reports');
    XLSX.writeFile(wb, 'reports.xlsx');
  };

  const clearSearch = () => {
    setSearchTerm('');
    switch (activeTab) {
      case 'courses':
        setFilteredCourses(courses);
        break;
      case 'reports':
        setFilteredReports(reports);
        break;
      case 'classes':
        setFilteredClasses(classes);
        break;
      case 'lecturers':
        setFilteredLecturers(lecturers);
        break;
      case 'rating':
        setFilteredRatings(ratings);
        break;
      default:
        break;
    }
  };

  const getSearchPlaceholder = () => {
    switch (activeTab) {
      case 'courses':
        return 'Search courses by name, code, stream, or description';
      case 'reports':
        return 'Search reports by lecturer, course, week, feedback, or status';
      case 'monitoring':
        return 'Search monitoring data';
      case 'classes':
        return 'Search classes by course, lecturer, venue, stream, or schedule';
      case 'lecturers':
        return 'Search lecturers by username, stream, or email';
      case 'rating':
        return 'Search ratings by type, comment, rating, or date';
      default:
        return 'Search...';
    }
  };

  const getResultCount = () => {
    switch (activeTab) {
      case 'courses':
        return { filtered: filteredCourses.length, total: courses.length };
      case 'reports':
        return { filtered: filteredReports.length, total: reports.length };
      case 'classes':
        return { filtered: filteredClasses.length, total: classes.length };
      case 'lecturers':
        return { filtered: filteredLecturers.length, total: lecturers.length };
      case 'rating':
        return { filtered: filteredRatings.length, total: ratings.length };
      default:
        return { filtered: 0, total: 0 };
    }
  };

  const resultCount = getResultCount();

  return (
    <Container className="dashboard-container">
      <div className="dashboard-header mb-4">
        <div className="d-flex justify-content-between align-items-center">
          <div>
            <h1 className="dashboard-title">Program Leader Dashboard</h1>
            <p className="dashboard-subtitle">Manage courses, assignments, and faculty analytics</p>
          </div>
          <Button as={Link} to="/" variant="outline-primary" className="btn-custom">
            ← Back to Home
          </Button>
        </div>
      </div>

      <Nav variant="tabs" className="dashboard-nav" activeKey={activeTab} onSelect={setActiveTab}>
        <Nav.Item><Nav.Link eventKey="courses">Courses</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="reports">Reports</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="monitoring">Monitoring</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="classes">Classes</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="lecturers">Lecturers</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="rating">Rating</Nav.Link></Nav.Item>
      </Nav>

      {/* Enhanced Search Bar */}
      <div className="search-section mb-4">
        <InputGroup>
          <Form.Control
            placeholder={getSearchPlaceholder()}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Button variant="outline-secondary" onClick={clearSearch}>
            Clear
          </Button>
        </InputGroup>
        {searchTerm && (
          <div className="mt-2">
            <small className="text-muted">
              Found {resultCount.filtered} of {resultCount.total} results
            </small>
          </div>
        )}
      </div>

      <div className="dashboard-content">
        {activeTab === 'courses' && (
          <div id="courses">
            <h2 className="mb-4">Add Courses & Assign Lecturers</h2>
            
            {/* Forms remain the same */}
            <Card className="card-custom mb-4">
              <Card.Body>
                <h4 className="mb-3">Add New Course</h4>
                <Form onSubmit={handleCourseSubmit}>
                  {/* Form content remains the same */}
                </Form>
              </Card.Body>
            </Card>

            <Card className="card-custom mb-4">
              <Card.Body>
                <h4 className="mb-3">Assign Lecturer to Course</h4>
                <Form onSubmit={handleAssignSubmit}>
                  {/* Form content remains the same */}
                </Form>
              </Card.Body>
            </Card>

            <div className="d-flex justify-content-between align-items-center mb-3">
              <h3>Current Course Assignments</h3>
              <div className="text-muted">
                Showing {filteredClasses.length} of {classes.length} assignments
              </div>
            </div>

            {filteredClasses.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No assignments found matching your search.' : 'No course assignments yet.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Table striped bordered hover className="table-custom">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Stream</th>
                    <th>Venue</th>
                    <th>Schedule</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredClasses.map(c => (
                    <tr key={c.id}>
                      <td>{c.course_name}</td>
                      <td>{c.lecturer_name}</td>
                      <td>{c.stream}</td>
                      <td>{c.venue}</td>
                      <td>{c.scheduled_time}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </div>
        )}

        {activeTab === 'reports' && (
          <div id="reports">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Reports from PRLs</h2>
              <div className="d-flex align-items-center gap-2">
                <div className="text-muted">
                  Showing {filteredReports.length} of {reports.length} reports
                </div>
                <Button onClick={exportToExcel} className="btn-custom">Export to Excel</Button>
              </div>
            </div>

            {filteredReports.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No reports found matching your search.' : 'No reports available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Table striped bordered hover className="table-custom">
                <thead>
                  <tr>
                    <th>Lecturer</th>
                    <th>Course</th>
                    <th>Week</th>
                    <th>Feedback</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map(r => (
                    <tr key={r.id}>
                      <td>{r.lecturer_name}</td>
                      <td>{r.course_name}</td>
                      <td>{r.week}</td>
                      <td>{r.prl_feedback || 'No feedback yet'}</td>
                      <td>
                        <span className={`badge ${r.prl_feedback ? 'bg-success' : 'bg-warning'}`}>
                          {r.prl_feedback ? 'Reviewed' : 'Pending'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </div>
        )}

        {activeTab === 'monitoring' && (
          <div id="monitoring">
            <h2 className="mb-4">Faculty-Wide Analytics</h2>
            <Row className="mb-4">
              <Col md={6}>
                <Card className="card-custom text-center">
                  <Card.Body>
                    <h3 className="text-gradient">{monitoring.avg_attendance}%</h3>
                    <p className="mb-0">Average Attendance</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="card-custom text-center">
                  <Card.Body>
                    <h3 className="text-gradient">{monitoring.report_count}</h3>
                    <p className="mb-0">Total Reports</p>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </div>
        )}

        {activeTab === 'classes' && (
          <div id="classes">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Manage Classes Across Department</h2>
              <div className="text-muted">
                Showing {filteredClasses.length} of {classes.length} classes
              </div>
            </div>
            {filteredClasses.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No classes found matching your search.' : 'No classes available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Table striped bordered hover className="table-custom">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Venue</th>
                    <th>Schedule</th>
                    <th>Stream</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredClasses.map(c => (
                    <tr key={c.id}>
                      <td>{c.course_name}</td>
                      <td>{c.lecturer_name}</td>
                      <td>{c.venue}</td>
                      <td>{c.scheduled_time}</td>
                      <td>{c.stream}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </div>
        )}

        {activeTab === 'lecturers' && (
          <div id="lecturers">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Manage Lecturer Schedules</h2>
              <div className="text-muted">
                Showing {filteredLecturers.length} of {lecturers.length} lecturers
              </div>
            </div>
            {filteredLecturers.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No lecturers found matching your search.' : 'No lecturers available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Row>
                {filteredLecturers.map(l => (
                  <Col md={6} lg={4} key={l.id} className="mb-3">
                    <Card className="card-custom h-100">
                      <Card.Body>
                        <h5 className="card-title">{l.username}</h5>
                        <p className="card-text">
                          <strong>Stream:</strong> {l.stream || 'Not assigned'}
                        </p>
                        <p className="card-text">
                          <strong>Email:</strong> {l.email || 'N/A'}
                        </p>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}
          </div>
        )}

        {activeTab === 'rating' && (
          <div id="rating">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Overall Ratings & Feedback</h2>
              <div className="text-muted">
                Showing {filteredRatings.length} of {ratings.length} ratings
              </div>
            </div>
            {filteredRatings.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No ratings found matching your search.' : 'No ratings available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Row>
                {filteredRatings.map(r => (
                  <Col md={6} key={r.id} className="mb-3">
                    <Card className="card-custom h-100">
                      <Card.Body>
                        <div className="d-flex justify-content-between align-items-start mb-2">
                          <h6 className="card-title text-capitalize">{r.type?.replace(/_/g, ' ')}</h6>
                          <span className="badge bg-primary">Rating: {r.rating}/5</span>
                        </div>
                        <p className="card-text">{r.comment}</p>
                        <small className="text-muted">
                          {r.created_at ? new Date(r.created_at).toLocaleDateString() : 'Recent'}
                        </small>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}
          </div>
        )}
      </div>
    </Container>
  );
};

export default PlDashboard;
