import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Nav, Table, Form, Button, ListGroup, Card, Row, Col, InputGroup } from 'react-bootstrap';
import { toast } from 'react-toastify';
import * as XLSX from 'xlsx';
import { Link } from 'react-router-dom';

const LecturerDashboard = () => {
  const [classes, setClasses] = useState([]);
  const [filteredClasses, setFilteredClasses] = useState([]);
  const [reports, setReports] = useState([]);
  const [filteredReports, setFilteredReports] = useState([]);
  const [monitoring, setMonitoring] = useState({ avg_attendance: 0, report_count: 0 });
  const [ratings, setRatings] = useState([]);
  const [filteredRatings, setFilteredRatings] = useState([]);
  const [formData, setFormData] = useState({ 
    class_id: '', week: '', date: '', topic: '', learning_outcomes: '', 
    present_students: '', total_students: '', venue: '', scheduled_time: '', recommendations: '' 
  });
  const [facilityRating, setFacilityRating] = useState(1);
  const [facilityComment, setFacilityComment] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('classes');

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        toast.error('No token found. Please log in again.');
        return;
      }
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const resClasses = await axios.get('http://localhost:5000/classes', config);
        setClasses(resClasses.data || []);
        setFilteredClasses(resClasses.data || []);
        const resReports = await axios.get('http://localhost:5000/reports', config);
        setReports(resReports.data || []);
        setFilteredReports(resReports.data || []);
        const resMonitoring = await axios.get('http://localhost:5000/monitoring', config);
        setMonitoring(resMonitoring.data || { avg_attendance: 0, report_count: 0 });
        const resRatings = await axios.get('http://localhost:5000/ratings', config);
        setRatings(resRatings.data || []);
        setFilteredRatings(resRatings.data || []);
      } catch (err) {
        console.error('Fetch error in LecturerDashboard:', err.response?.data || err.message);
        toast.error(err.response?.data?.message || 'Error fetching data');
      }
    };
    fetchData();
  }, []);

  // Enhanced search functionality
  useEffect(() => {
    if (activeTab === 'classes') {
      const filtered = classes.filter(classItem =>
        classItem.course_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.venue?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.stream?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.scheduled_time?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredClasses(filtered);
    } else if (activeTab === 'reports') {
      const filtered = reports.filter(report =>
        report.topic?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.date?.includes(searchTerm) ||
        report.week?.toString().includes(searchTerm) ||
        report.status?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredReports(filtered);
    } else if (activeTab === 'monitoring') {
      const studentRatings = ratings.filter(r => r.type === 'student_to_lecturer');
      const filtered = studentRatings.filter(rating =>
        rating.comment?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        rating.rating?.toString().includes(searchTerm) ||
        rating.created_at?.includes(searchTerm)
      );
      setFilteredRatings(filtered);
    } else if (activeTab === 'rating') {
      const facilityRatings = ratings.filter(r => r.type === 'lecturer_to_facilities');
      const filtered = facilityRatings.filter(rating =>
        rating.comment?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        rating.rating?.toString().includes(searchTerm) ||
        rating.created_at?.includes(searchTerm)
      );
      setFilteredRatings(filtered);
    }
  }, [searchTerm, classes, reports, ratings, activeTab]);

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleReportSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return toast.error('No token found');
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post('http://localhost:5000/reports', formData, config);
      toast.success('Report submitted successfully');
      setFormData({ 
        class_id: '', week: '', date: '', topic: '', learning_outcomes: '', 
        present_students: '', total_students: '', venue: '', scheduled_time: '', recommendations: '' 
      });
      const resReports = await axios.get('http://localhost:5000/reports', config);
      setReports(resReports.data || []);
      setFilteredReports(resReports.data || []);
    } catch (err) {
      console.error('Report submit error:', err.response?.data);
      toast.error(err.response?.data?.message || 'Error submitting report');
    }
  };

  const handleRatingSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return toast.error('No token found');
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post('http://localhost:5000/ratings', { 
        ratee_id: null, 
        rating: facilityRating, 
        comment: facilityComment, 
        type: 'lecturer_to_facilities' 
      }, config);
      toast.success('Rating submitted successfully');
      setFacilityRating(1);
      setFacilityComment('');
      const resRatings = await axios.get('http://localhost:5000/ratings', config);
      setRatings(resRatings.data || []);
      setFilteredRatings(resRatings.data.filter(r => r.type === 'lecturer_to_facilities'));
    } catch (err) {
      console.error('Rating submit error:', err.response?.data);
      toast.error(err.response?.data?.message || 'Error submitting rating');
    }
  };

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filteredReports);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Reports');
    XLSX.writeFile(wb, 'reports.xlsx');
  };

  const clearSearch = () => {
    setSearchTerm('');
    if (activeTab === 'classes') {
      setFilteredClasses(classes);
    } else if (activeTab === 'reports') {
      setFilteredReports(reports);
    } else {
      const filtered = ratings.filter(r => 
        activeTab === 'monitoring' ? r.type === 'student_to_lecturer' : r.type === 'lecturer_to_facilities'
      );
      setFilteredRatings(filtered);
    }
  };

  const getSearchPlaceholder = () => {
    switch (activeTab) {
      case 'classes':
        return 'Search classes by course, venue, stream, or schedule';
      case 'reports':
        return 'Search reports by topic, date, week, or status';
      case 'monitoring':
        return 'Search student feedback by comment, rating, or date';
      case 'rating':
        return 'Search your facility ratings by comment, rating, or date';
      default:
        return 'Search...';
    }
  };

  const getResultCount = () => {
    switch (activeTab) {
      case 'classes':
        return { filtered: filteredClasses.length, total: classes.length };
      case 'reports':
        return { filtered: filteredReports.length, total: reports.length };
      case 'monitoring':
        return { filtered: filteredRatings.length, total: ratings.filter(r => r.type === 'student_to_lecturer').length };
      case 'rating':
        return { filtered: filteredRatings.length, total: ratings.filter(r => r.type === 'lecturer_to_facilities').length };
      default:
        return { filtered: 0, total: 0 };
    }
  };

  const resultCount = getResultCount();

  return (
    <Container className="dashboard-container">
      <div className="dashboard-header mb-4">
        <div className="d-flex justify-content-between align-items-center">
          <div>
            <h1 className="dashboard-title">Lecturer Dashboard</h1>
            <p className="dashboard-subtitle">Manage your classes and submit teaching reports</p>
          </div>
          <Button as={Link} to="/" variant="outline-primary" className="btn-custom">
            ← Back to Home
          </Button>
        </div>
      </div>

      <Nav variant="tabs" className="dashboard-nav" activeKey={activeTab} onSelect={setActiveTab}>
        <Nav.Item><Nav.Link eventKey="classes">Classes</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="reports">Reports</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="monitoring">Monitoring</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="rating">Rating</Nav.Link></Nav.Item>
      </Nav>

      {/* Enhanced Search Bar */}
      <div className="search-section mb-4">
        <InputGroup>
          <Form.Control
            placeholder={getSearchPlaceholder()}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Button variant="outline-secondary" onClick={clearSearch}>
            Clear
          </Button>
        </InputGroup>
        {searchTerm && (
          <div className="mt-2">
            <small className="text-muted">
              Found {resultCount.filtered} of {resultCount.total} results
            </small>
          </div>
        )}
      </div>

      <div className="dashboard-content">
        {activeTab === 'classes' && (
          <div id="classes">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Your Assigned Classes</h2>
              <div className="text-muted">
                Showing {filteredClasses.length} of {classes.length} classes
              </div>
            </div>
            {filteredClasses.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No classes found matching your search.' : 'No classes assigned.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Table striped bordered hover className="table-custom">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Venue</th>
                    <th>Schedule</th>
                    <th>Total Students</th>
                    <th>Stream</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredClasses.map(c => (
                    <tr key={c.id}>
                      <td>{c.course_name}</td>
                      <td>{c.venue}</td>
                      <td>{c.scheduled_time}</td>
                      <td>{c.total_students}</td>
                      <td>{c.stream}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </div>
        )}

        {/* Rest of the component remains the same but uses filtered data */}
        {activeTab === 'reports' && (
          <div id="reports">
            <h2 className="mb-4">Submit Teaching Report</h2>
            
            <Card className="card-custom mb-4">
              <Card.Body>
                <Form onSubmit={handleReportSubmit}>
                  {/* Form content remains the same */}
                </Form>
              </Card.Body>
            </Card>

            <div className="d-flex justify-content-between align-items-center mb-4">
              <h3>Your Previous Reports</h3>
              <div className="d-flex align-items-center gap-2">
                <div className="text-muted">
                  Showing {filteredReports.length} of {reports.length} reports
                </div>
                <Button onClick={exportToExcel} className="btn-custom">Export to Excel</Button>
              </div>
            </div>

            {filteredReports.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No reports found matching your search.' : 'No reports submitted yet.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Table striped bordered hover className="table-custom">
                <thead>
                  <tr>
                    <th>Week</th>
                    <th>Date</th>
                    <th>Topic</th>
                    <th>Attendance</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map(r => (
                    <tr key={r.id}>
                      <td>{r.week}</td>
                      <td>{r.date}</td>
                      <td>{r.topic}</td>
                      <td>{r.present_students}/{r.total_students}</td>
                      <td>
                        <span className={`badge ${r.status === 'reviewed' ? 'bg-success' : 'bg-warning'}`}>
                          {r.status || 'pending'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </div>
        )}

        {activeTab === 'monitoring' && (
          <div id="monitoring">
            <h2 className="mb-4">Attendance Stats & Student Feedback</h2>
            
            <Row className="mb-4">
              <Col md={6}>
                <Card className="card-custom text-center">
                  <Card.Body>
                    <h3 className="text-gradient">{monitoring.avg_attendance}%</h3>
                    <p className="mb-0">Average Attendance Rate</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="card-custom text-center">
                  <Card.Body>
                    <h3 className="text-gradient">{monitoring.report_count}</h3>
                    <p className="mb-0">Total Reports Submitted</p>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            <div className="d-flex justify-content-between align-items-center mb-3">
              <h3>Student Feedback</h3>
              <div className="text-muted">
                Showing {filteredRatings.length} of {ratings.filter(r => r.type === 'student_to_lecturer').length} feedback items
              </div>
            </div>

            {filteredRatings.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No feedback found matching your search.' : 'No student feedback yet.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <ListGroup>
                {filteredRatings.map(r => (
                  <ListGroup.Item key={r.id} className="card-custom mb-2">
                    <div className="d-flex justify-content-between align-items-start">
                      <div>
                        <div className="d-flex align-items-center mb-2">
                          <strong className="me-2">Rating: {r.rating}/5</strong>
                          <div className="text-warning">
                            {'★'.repeat(r.rating)}{'☆'.repeat(5 - r.rating)}
                          </div>
                        </div>
                        <p className="mb-0">{r.comment}</p>
                      </div>
                      <small className="text-muted">
                        {r.created_at ? new Date(r.created_at).toLocaleDateString() : 'Recent'}
                      </small>
                    </div>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            )}
          </div>
        )}

        {activeTab === 'rating' && (
          <div id="rating">
            <h2 className="mb-4">Rate Facilities & Resources</h2>
            
            <Card className="card-custom mb-4">
              <Card.Body>
                <Form onSubmit={handleRatingSubmit}>
                  {/* Form content remains the same */}
                </Form>
              </Card.Body>
            </Card>

            <div className="d-flex justify-content-between align-items-center mb-3">
              <h3>Your Previous Facility Ratings</h3>
              <div className="text-muted">
                Showing {filteredRatings.length} of {ratings.filter(r => r.type === 'lecturer_to_facilities').length} ratings
              </div>
            </div>

            {filteredRatings.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No ratings found matching your search.' : 'No facility ratings submitted yet.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              filteredRatings.map(r => (
                <Card key={r.id} className="card-custom mb-2">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <div className="d-flex align-items-center mb-1">
                          <strong className="me-2">Rating: {r.rating}/5</strong>
                          <div className="text-warning">
                            {'★'.repeat(r.rating)}{'☆'.repeat(5 - r.rating)}
                          </div>
                        </div>
                        <p className="mb-0">{r.comment}</p>
                      </div>
                      <small className="text-muted">
                        {r.created_at ? new Date(r.created_at).toLocaleDateString() : 'Recent'}
                      </small>
                    </div>
                  </Card.Body>
                </Card>
              ))
            )}
          </div>
        )}
      </div>
    </Container>
  );
};

export default LecturerDashboard;
