import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Nav, Table, Form, Button, ListGroup, Card, Row, Col, InputGroup } from 'react-bootstrap';
import { toast } from 'react-toastify';
import * as XLSX from 'xlsx';
import { Link } from 'react-router-dom';

const PrlDashboard = () => {
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [classes, setClasses] = useState([]);
  const [filteredClasses, setFilteredClasses] = useState([]);
  const [reports, setReports] = useState([]);
  const [filteredReports, setFilteredReports] = useState([]);
  const [monitoring, setMonitoring] = useState({ avg_attendance: 0, report_count: 0 });
  const [ratings, setRatings] = useState([]);
  const [filteredRatings, setFilteredRatings] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [selectedReportId, setSelectedReportId] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('courses');

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        toast.error('No token found. Please log in again.');
        return;
      }
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const resCourses = await axios.get('http://localhost:5000/courses', config);
        setCourses(resCourses.data || []);
        setFilteredCourses(resCourses.data || []);
        const resClasses = await axios.get('http://localhost:5000/classes', config);
        setClasses(resClasses.data || []);
        setFilteredClasses(resClasses.data || []);
        const resReports = await axios.get('http://localhost:5000/reports', config);
        setReports(resReports.data || []);
        setFilteredReports(resReports.data || []);
        const resMonitoring = await axios.get('http://localhost:5000/monitoring', config);
        setMonitoring(resMonitoring.data || { avg_attendance: 0, report_count: 0 });
        const resRatings = await axios.get('http://localhost:5000/ratings', config);
        setRatings(resRatings.data || []);
        setFilteredRatings(resRatings.data || []);
      } catch (err) {
        console.error('Fetch error in PrlDashboard:', err.response?.data || err.message);
        toast.error(err.response?.data?.message || 'Error fetching data');
      }
    };
    fetchData();
  }, []);

  // Enhanced search functionality
  useEffect(() => {
    if (activeTab === 'courses') {
      const filtered = courses.filter(course =>
        course.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.code?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.stream?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.description?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredCourses(filtered);
    } else if (activeTab === 'reports') {
      const filtered = reports.filter(report =>
        report.lecturer_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.course_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.topic?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.week?.toString().includes(searchTerm) ||
        report.status?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        report.prl_feedback?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredReports(filtered);
    } else if (activeTab === 'classes') {
      const filtered = classes.filter(classItem =>
        classItem.course_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.lecturer_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.venue?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.stream?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        classItem.scheduled_time?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredClasses(filtered);
    } else if (activeTab === 'rating') {
      const filtered = ratings.filter(rating =>
        rating.type?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        rating.comment?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        rating.rating?.toString().includes(searchTerm) ||
        rating.created_at?.includes(searchTerm)
      );
      setFilteredRatings(filtered);
    }
  }, [searchTerm, courses, reports, classes, ratings, activeTab]);

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return toast.error('No token found');
    if (!feedback.trim()) return toast.error('Please enter feedback');
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.put(`http://localhost:5000/reports/${selectedReportId}/feedback`, { feedback }, config);
      toast.success('Feedback added successfully');
      setFeedback('');
      setSelectedReportId('');
      const resReports = await axios.get('http://localhost:5000/reports', config);
      setReports(resReports.data || []);
      setFilteredReports(resReports.data || []);
    } catch (err) {
      console.error('Feedback submit error:', err.response?.data);
      toast.error(err.response?.data?.message || 'Error adding feedback');
    }
  };

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filteredReports);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Reports');
    XLSX.writeFile(wb, 'reports.xlsx');
  };

  const clearSearch = () => {
    setSearchTerm('');
    switch (activeTab) {
      case 'courses':
        setFilteredCourses(courses);
        break;
      case 'reports':
        setFilteredReports(reports);
        break;
      case 'classes':
        setFilteredClasses(classes);
        break;
      case 'rating':
        setFilteredRatings(ratings);
        break;
      default:
        break;
    }
  };

  const getSearchPlaceholder = () => {
    switch (activeTab) {
      case 'courses':
        return 'Search courses by name, code, stream, or description';
      case 'reports':
        return 'Search reports by lecturer, course, topic, week, status, or feedback';
      case 'classes':
        return 'Search classes by course, lecturer, venue, stream, or schedule';
      case 'rating':
        return 'Search ratings by type, comment, rating, or date';
      default:
        return 'Search...';
    }
  };

  const getResultCount = () => {
    switch (activeTab) {
      case 'courses':
        return { filtered: filteredCourses.length, total: courses.length };
      case 'reports':
        return { filtered: filteredReports.length, total: reports.length };
      case 'classes':
        return { filtered: filteredClasses.length, total: classes.length };
      case 'rating':
        return { filtered: filteredRatings.length, total: ratings.length };
      default:
        return { filtered: 0, total: 0 };
    }
  };

  const resultCount = getResultCount();

  return (
    <Container className="dashboard-container">
      <div className="dashboard-header mb-4">
        <div className="d-flex justify-content-between align-items-center">
          <div>
            <h1 className="dashboard-title">PRL Dashboard</h1>
            <p className="dashboard-subtitle">Review lecturer reports and provide feedback</p>
          </div>
          <Button as={Link} to="/" variant="outline-primary" className="btn-custom">
             Back to Home
          </Button>
        </div>
      </div>

      <Nav variant="tabs" className="dashboard-nav" activeKey={activeTab} onSelect={setActiveTab}>
        <Nav.Item><Nav.Link eventKey="courses">Courses</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="reports">Reports</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="monitoring">Monitoring</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="rating">Rating</Nav.Link></Nav.Item>
        <Nav.Item><Nav.Link eventKey="classes">Classes</Nav.Link></Nav.Item>
      </Nav>

      {/* Enhanced Search Bar */}
      <div className="search-section mb-4">
        <InputGroup>
          <Form.Control
            placeholder={getSearchPlaceholder()}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Button variant="outline-secondary" onClick={clearSearch}>
            Clear
          </Button>
        </InputGroup>
        {searchTerm && (
          <div className="mt-2">
            <small className="text-muted">
              Found {resultCount.filtered} of {resultCount.total} results
            </small>
          </div>
        )}
      </div>

      <div className="dashboard-content">
        {activeTab === 'courses' && (
          <div id="courses">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Courses Under Your Stream</h2>
              <div className="text-muted">
                Showing {filteredCourses.length} of {courses.length} courses
              </div>
            </div>
            {filteredCourses.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No courses found matching your search.' : 'No courses available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Row>
                {filteredCourses.map(c => {
                  const pendingReports = reports.filter(r => r.course_name === c.name && r.status === 'pending');
                  return (
                    <Col md={6} lg={4} key={c.id} className="mb-3">
                      <Card className={`card-custom h-100 ${pendingReports.length > 0 ? 'border-warning' : ''}`}>
                        <Card.Body>
                          <h5 className="card-title">{c.name}</h5>
                          <p className="card-text">
                            <strong>Code:</strong> {c.code}
                          </p>
                          <p className="card-text">
                            <strong>Stream:</strong> {c.stream}
                          </p>
                          <div className={`badge ${pendingReports.length > 0 ? 'bg-warning' : 'bg-success'}`}>
                            {pendingReports.length > 0 ? `${pendingReports.length} Pending` : 'Reviewed'}
                          </div>
                        </Card.Body>
                      </Card>
                    </Col>
                  );
                })}
              </Row>
            )}
          </div>
        )}

        {activeTab === 'reports' && (
          <div id="reports">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Lecturer Reports</h2>
              <div className="d-flex align-items-center gap-2">
                <div className="text-muted">
                  Showing {filteredReports.length} of {reports.length} reports
                </div>
                <Button onClick={exportToExcel} className="btn-custom">Export to Excel</Button>
              </div>
            </div>

            {filteredReports.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No reports found matching your search.' : 'No reports available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <>
                <Table striped bordered hover className="table-custom mb-4">
                  <thead>
                    <tr>
                      <th>Lecturer</th>
                      <th>Course</th>
                      <th>Week</th>
                      <th>Topic</th>
                      <th>Attendance</th>
                      <th>Status</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredReports.map(r => (
                      <tr key={r.id}>
                        <td>{r.lecturer_name}</td>
                        <td>{r.course_name}</td>
                        <td>{r.week}</td>
                        <td>{r.topic}</td>
                        <td>{r.present_students}/{r.total_students}</td>
                        <td>
                          <span className={`badge ${r.status === 'reviewed' ? 'bg-success' : 'bg-warning'}`}>
                            {r.status || 'pending'}
                          </span>
                        </td>
                        <td>
                          <Button 
                            size="sm" 
                            className="btn-custom"
                            onClick={() => setSelectedReportId(r.id)}
                          >
                            Add Feedback
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>

                {selectedReportId && (
                  <Card className="card-custom">
                    <Card.Body>
                      <h5 className="mb-3">Add Feedback for Selected Report</h5>
                      <Form onSubmit={handleFeedbackSubmit}>
                        <Form.Group className="mb-3">
                          <Form.Label>Your Feedback</Form.Label>
                          <Form.Control 
                            className="form-control-custom"
                            as="textarea" 
                            rows={4}
                            value={feedback} 
                            onChange={e => setFeedback(e.target.value)} 
                            placeholder="Enter your feedback for this report..."
                          />
                        </Form.Group>
                        <div className="d-flex gap-2">
                          <Button type="submit" className="btn-custom">Submit Feedback</Button>
                          <Button 
                            variant="outline-secondary" 
                            onClick={() => {
                              setSelectedReportId('');
                              setFeedback('');
                            }}
                          >
                            Cancel
                          </Button>
                        </div>
                      </Form>
                    </Card.Body>
                  </Card>
                )}

                <h3 className="mt-4 mb-3">Feedback History</h3>
                <ListGroup>
                  {filteredReports.filter(r => r.prl_feedback).map(r => (
                    <ListGroup.Item key={r.id} className="card-custom mb-2">
                      <div className="d-flex justify-content-between align-items-start">
                        <div>
                          <strong>{r.course_name}</strong> - Week {r.week}
                          <p className="mb-0 mt-1">{r.prl_feedback}</p>
                        </div>
                        <small className="text-muted">{r.lecturer_name}</small>
                      </div>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              </>
            )}
          </div>
        )}

        {activeTab === 'monitoring' && (
          <div id="monitoring">
            <h2 className="mb-4">Attendance and Reporting Trends</h2>
            <Row>
              <Col md={6}>
                <Card className="card-custom text-center">
                  <Card.Body>
                    <h3 className="text-gradient">{monitoring.avg_attendance}%</h3>
                    <p className="mb-0">Average Attendance Rate</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="card-custom text-center">
                  <Card.Body>
                    <h3 className="text-gradient">{monitoring.report_count}</h3>
                    <p className="mb-0">Total Reports Submitted</p>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </div>
        )}

        {activeTab === 'rating' && (
          <div id="rating">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Teaching Quality & Resource Feedback</h2>
              <div className="text-muted">
                Showing {filteredRatings.length} of {ratings.length} ratings
              </div>
            </div>
            {filteredRatings.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No ratings found matching your search.' : 'No ratings available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Row>
                {filteredRatings.map(r => (
                  <Col md={6} key={r.id} className="mb-3">
                    <Card className="card-custom h-100">
                      <Card.Body>
                        <div className="d-flex justify-content-between align-items-start mb-2">
                          <h6 className="card-title text-capitalize">
                            {r.type?.replace(/_/g, ' ') || 'General Rating'}
                          </h6>
                          <span className="badge bg-primary">Rating: {r.rating}/5</span>
                        </div>
                        <p className="card-text">{r.comment}</p>
                        <small className="text-muted">
                          {r.created_at ? new Date(r.created_at).toLocaleDateString() : 'Recent'}
                        </small>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}
          </div>
        )}

        {activeTab === 'classes' && (
          <div id="classes">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Assigned Classes and Teaching Allocations</h2>
              <div className="text-muted">
                Showing {filteredClasses.length} of {classes.length} classes
              </div>
            </div>
            {filteredClasses.length === 0 ? (
              <Card className="text-center py-4">
                <Card.Body>
                  <p className="text-muted mb-0">
                    {searchTerm ? 'No classes found matching your search.' : 'No classes available.'}
                  </p>
                </Card.Body>
              </Card>
            ) : (
              <Table striped bordered hover className="table-custom">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Venue</th>
                    <th>Schedule</th>
                    <th>Stream</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredClasses.map(c => (
                    <tr key={c.id}>
                      <td>{c.course_name}</td>
                      <td>{c.lecturer_name}</td>
                      <td>{c.venue}</td>
                      <td>{c.scheduled_time}</td>
                      <td>{c.stream}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </div>
        )}
      </div>
    </Container>
  );
};

export default PrlDashboard;
